package class01;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double n1=34.5;
		double n2=4.5;
		
		if(n1>n2) {
			System.out.println("Double value "+ n1+ " is larger than "+n2);
		}else {
			System.out.println("Double value "+n2+" is larger than "+n1);
		}
	}

}
